INK.BinChart.prototype.init = function() {
  var that = this;

  for (var i=0; i < this.data.length; i++) {
    var s = new INK.State(this.data[i], this.data[i]['postal'], i, this.useCircleImages);
    s.rating = this.data[i]['ratings_2012'];
    s.setFill(this.data[i]['ratings_2012'])
    s.setStroke(this.data[i]['ratings_2012'])
    if(INK.isiPad) {
      s.el[0].style.left = (s.data.mapx + this.mapOffsetX)+"px";
      s.el[0].style.top = (s.data.mapy)+"px";
    }
    this.states.push(s);
    this.x = this.states[i].data.mapx + this.mapOffsetX;
    this.y = this.states[i].data.mapy;
    this.canvas.append(s.el);
  };


  this.force = d3.layout.force()
    .nodes(this.states)
    .gravity(0)
    .charge(0)
    .size([this.width, this.height]);


  // this.states.enter()
  //   .style('top', )

  // this.setBins("ratings_2012");

  // Setting the start position for the map
  for (var i = this.states.length - 1; i >= 0; i--) {
    this.states[i].x = this.states[i].data.mapx + this.mapOffsetX;
    this.states[i].y = this.states[i].data.mapy;
  };

  this.force.start();

  this.$("#canvas").addClass("ready");

  // this.squares.call(this.force.drag)

};

INK.BinChart.prototype.setBins = function(prop, wait, save) {
  this.mode = prop;
  var i, val, s;
  for (i = this.states.length - 1; i >= 0; i--){
    s = this.states[i]
    val = s.data[prop]
    if (val > 0) {
      s.bin = 1;
    } else if (val < 0) {
      s.bin = -1
    } else if (val === 0) {
      s.bin = 0
    };

    if (save) s.savedBin = s.bin;
  };
  this.updateCounts();
  this.force.resume();
}

// Set the bins to what the reader's URL says they are ... or to what the reader
// has saved.
INK.BinChart.prototype.setReaderBins = function() {

  // Start with our default bins.
  if (!this.savedStates) {
    this.setBins('ratings_2012', true);

    // Load from the URL, if it's our first load.
    if (INK.saver.hasFragment()) {
      this.setBins('ratings_2012', true);
      var changes = INK.saver.getRatings();
      for (var i = 0, l = changes.length; i < l; i++) {
        var change = changes[i];
        var state = _.find(this.states, function(s){ return s.postal == change[0]; });
        state.bin = change[1];
      }
    }

    // Save the initial state.
    for (var i = 0, l = this.states.length; i < l; i++) {
      var state = this.states[i];
      state.savedBin = state.bin;
    }
  }

  // Load from the local save.
  if (this.savedStates) {
    for (var i = 0, l = this.states.length; i < l; i++) {
      var state = this.states[i];
      state.bin = state.savedBin;
    }
  }

  this.updateCounts();
  this.force.resume();
  this.mode = 'share';
  if (!INK.saver.shared) INK.saver.checkShareable();
  this.savedStates = true;
};

INK.BinChart.prototype.setBinsLoose = function(prop, wait, save) {
  this.mode = prop;

  var i, val, s;
  for (i = this.states.length - 1; i >= 0; i--){
    s = this.states[i]
    val = s.data[prop]
    if (val > 1) {
      s.bin = 1;
    } else if (val < -1) {
      s.bin = -1
    } else {
      s.bin = 0
    };

    if (save) s.savedBin = s.bin;
  };

  if (!wait) {
    this.updateCounts();
    this.force.resume();
  }
};


INK.BinChart.prototype.closeState = function(postal) {
  for (var i = this.states.length - 1; i >= 0; i--){
    if (this.states[i].postal === postal) {
      this.states[i].close();
      break;
    };
  };
}

INK.BinChart.prototype.shiftState = function(postal, amount) {
  for (var i = this.states.length - 1; i >= 0; i--){
    if (this.states[i].postal === postal) {
      this.states[i].bin += amount;
      this.force.resume();
    };
  };
  this.updateCounts();

};

INK.BinChart.prototype.updateCounts = function(options) {
  var i;
  this.binCounts = [0,0,0]
  for (i=0; i < this.states.length; i++){
    this.binCounts[this.states[i].bin + 1 ] += this.states[i].electoralVotes;
  };

  this.repOutput.html(this.wrapCharactersInTag(this.binCounts[0], "span", 3))
  this.demOutput.html(this.wrapCharactersInTag(this.binCounts[2], "span", 3))
  this.tossupOutput.html(this.binCounts[1])

  this.outputVotesToWin(this.binCounts[2], "dem")
  this.outputVotesToWin(this.binCounts[0], "rep")

  this.onUpdateCounts({
    demTotal: this.binCounts[2],
    repTotal: this.binCounts[0],
    tossupTotal: this.binCounts[1]
  });

};


INK.BinChart.prototype.outputVotesToWin = function(votes, party) {
  var remainingVotes = this.electoralVotesWin - votes;


  
  if (remainingVotes <= 0) {
    console.log('test ==> ',party,$j('.ink-'+party+'Well'))
    $j('.ink-demWell, .ink-repWell').removeClass('eg2012-winner');
    $j('.ink-'+party+'Well').addClass('eg2012-winner');    
    this.remainingOutputs[party].html("")
    this.remainingMessages[party].html("wins")
  } else if (this.binCounts[1] < remainingVotes ) {
    $j('.ink-'+party+'Well').removeClass('eg2012-winner');    
    this.remainingOutputs[party].html("")
    this.remainingMessages[party].html("")

  } else {
    $j('.ink-'+party+'Well').removeClass('eg2012-winner');        
    this.remainingOutputs[party].html("Needs "+String(this.electoralVotesWin - votes))
    this.remainingMessages[party].html("to win")
  };



}



INK.BinChart.prototype.wrapCharactersInTag = function(s, tag, places) {
  var arr = String(s).split("")
  if (arr.length < places) {
    for (var i=0; i <= (places - arr.length); i++) {
      arr.unshift("0")
    };

  };

  return this.$.map(arr, function(i){ return "<"+tag+">"+i+"</"+tag+">" }).join("")
}




INK.BinChart.prototype.set = function(options) {
  this.baseMode = options;
  var that = this;

  // MAP
  if (options == "map") {
    this.setBins("ratings_2012");
    this.draggingEnabled = false;
    this.force
      .charge(0)
      .friction(0)
      .on("tick", function(e){

        for (var i = that.states.length - 1; i >= 0; i--){
          that.sortMap(that.states[i], e.alpha)
          that.states[i].setPos();
        };

      })
      .start();

  // BINS
  } else if (options == "bins"){
    this.draggingEnabled = true;
    this.force
      .charge(this.defaultCharge)
      .friction(this.defaultFriction)
      .on("tick", function(e){

        for (var i = that.states.length - 1; i >= 0; i--){
          if (!that.states[i].isDragging) {
            that.sortBins(that.states[i], e.alpha);
            // that.buoyancy(that.states[i], e.alpha);

          };

          that.states[i].setPos();
        };

        // that.states
          // .each(that.sortBins(e.alpha))
          // .each(that.buoyancy(e.alpha))
          // .each(that.setPositions)
      })
      .start();
  }



};


INK.BinChart.prototype.setPositions = function(d) {
  d.setPos(Math.round(d.x), Math.round(d.y));
}

INK.BinChart.prototype.setBinFromXPosition = function(state) {
  var center = state.x + state.radius;
  if (center < this.demWidth) {
    state.bin = 1;
  } else if (center < this.demWidth + this.tossupWidth) {
    state.bin = 0;
  } else {
    state.bin = -1;
  }
  this.updateCounts();
  if (state.savedBin != state.bin) {
    state.savedBin = state.bin;
    INK.saver.checkShareableLater();
  }
}

INK.BinChart.prototype.sortMap = function(d, alpha) {
    var targetY = d.data.mapy;
    var targetX = d.data.mapx + this.mapOffsetX;

    // if (d.bin < 0 ) {
    //   targetX += 600;
    // } else if (d.bin > 0) {
    //   targetX += 0;
    // } else if (d.bin === 0) {
    //   targetX = 475;
    // }

    d.y += (targetY - d.y) * Math.sin(Math.PI * (1 - alpha*10)) / Math.sqrt(d.data.ev) * 2
    d.x += (targetX - d.x) * Math.sin(Math.PI * (1 - alpha*10)) / Math.sqrt(d.data.ev) * 2

};


INK.BinChart.prototype.sortBins = function(d, alpha) {
    var rating = d.bin
    var targetY = this.verticalCenter;
    var targetX = 300;

    if (rating > 0) {
      targetX = this.demCenter;
    } else if (rating < 0){
      targetX = this.repCenter;
    } else {
      targetX = this.tossupCenter;
    }

    d.y = d.y + (targetY - d.y) * (0.115) * alpha
    d.x = d.x + (targetX - d.x) * (0.115) * alpha

};


INK.BinChart.prototype.buoyancy = function(d, alpha) {
  var targetX;
  if (d.rating > 0) {
    targetX = this.demCenter;
  } else if (d.rating < 0){
    targetX = this.repCenter;
  } else {
    targetX = this.tossupCenter;
  }


  targetX -= d.rating  * 40;
  d.x = d.x + (targetX - d.x) * alpha * alpha * 0.1

}

