INK.saver = {

  // Copy of the original set of binned ratings, so that our URLs don't change.
  originalBins: {
    "AL":-1,"AK":-1,"AZ":-1,"AR":-1,"CA":1,"CO":0,"CT":1,"DE":1,"DC":1,"FL":0,
    "GA":-1,"HI":1,"ID":-1,"IL":1,"IN":-1,"IA":0,"KS":-1,"KY":-1,"LA":-1,"ME":1,
    "MD":1,"MA":1,"MI":1,"MN":1,"MS":-1,"MO":-1,"MT":-1,"NE":-1,"NV":0,"NH":0,
    "NJ":1,"NM":1,"NY":1,"NC":-1,"ND":-1,"OH":0,"OK":-1,"OR":1,"PA":0,"RI":1,"SC":-1,
    "SD":-1,"TN":-1,"TX":-1,"UT":-1,"VT":1,"VA":0,"WA":1,"WV":-1,"WI":0,"WY":-1,
    "NE_state": -1, "NE_1": -1, "NE_2": -1, "NE_3": -1,
    "ME_state": 1, "ME_1": 1, "ME_2": 1
  },

  labelToCode: {
    R: -1,
    T: 0,
    D: 1
  },

  codeToLabel: {
    '-1': 'R',
    '0': 'T',
    '1': 'D'
  },

  // Does the current URL have a saved set of states?
  hasFragment: function() {
    return window.location.hash.length > 2;
  },

  isDirectLinked: function() {
    return (/#?scenario$/).test(window.location.hash);
  },

  // How many states are still undecided?
  undecidedStateCount: function() {
    return _.select(INK.t.states, function(s){ return s.bin == 0; }).length;
  },

  // Toggle the share info...
  checkShareable: function() {
    var left = this.undecidedStateCount();
    if (this.shared && left > 0) {
      INK.$('.ink-shared').remove();
      this.shared = false;
    }
    var shareable = !!((INK.t.mode == 'share') && !this.shared);
    var desc = INK.$('.ink-shareDesc');
    var permasave = INK.$('.ink-permasave');
    INK.$('.ink-stateCount').text(left);
    INK.$('.ink-stateNoun').text(left == 1 ? 'state' : 'states');
    desc.toggle(shareable && left > 0);
    permasave.toggle(shareable && left == 0);
    if (shareable) {
      if (left > 0) {
        INK.$('.ink-permasave').remove();
        this.finished = false;
      } else if (!this.finished) {
        this.renderPermalink();
      }
    }
  },

  // Generate the hash fragment for the current choices.
  fragment: function() {
    var states = INK.t.states;
    var changed = [];
    for (var i = 0, l = states.length; i < l; i++) {
      var state = states[i];
      if (state.bin != this.originalBins[state.postal]) {
        changed.push(state.postal + '-' + this.codeToLabel[state.bin]);
      }
    }
    return changed.join(',');
  },

  // Load the state ratings from the URL.
  getRatings: function() {
    if (!this.hasFragment() || this.isDirectLinked()) return [];
    var frag = window.location.hash.replace(/^#/, '');
    return _.map(frag.split(','), _.bind(function(key) {
      var pair = key.split('-');
      return [pair[0], this.labelToCode[pair[1]]];
    }, this));
  },

  // Stash the current URL in the browser's location bar.
  setUrl: function() {
    window.location.hash = this.fragment();
  },

  renderPermalink : function() {
    var url = window.location.toString().replace(/#.*$/, '') + '#' + this.fragment();
    var encoded = encodeURIComponent(url);
    var demCount = parseInt(INK.$('.ink-dem-count').text(), 10);
    var repCount = parseInt(INK.$('.ink-rep-count').text(), 10);
    var message = "In my scenario for the 2012 election, " +
      (demCount == repCount ? ("Obama and Romney tie.") :
        ((repCount > demCount ? "Romney wins, with " + repCount : "Obama wins, with " + demCount) + " electoral votes."));
    var twitterMessage = encodeURIComponent(message + " What's yours?");
    var facebookMessage = encodeURIComponent(message + " See my map and create your own.");
    var html = '<div class="ink-permasave">';
    html += '<div class="ink-permalabel">Share your election scenario</div>';
    html += '<a class="nytg-share nytg-permalink-share nytg-column" href="' + url + '">Link</a>';
    html += '<a class="nytg-share nytg-facebook-share nytg-column" target="_blank" href="http://www.facebook.com/share.php?u=' + encoded + '&amp;t=' + facebookMessage + '">Facebook</a>';
    html += '<a class="nytg-share nytg-twitter-share nytg-column" target="_blank" href="https://twitter.com/intent/tweet?via=nytimes&amp;url=' + encoded + '&amp;text=' + twitterMessage + '">Twitter</a>';
    html += '</div>';
    INK.$('.ink-shareDesc').hide().after(html);
    this.finished = true;
  },

  renderExistingShare: function() {
    this.shared = true;
    var html = '<div class="ink-shared">You&rsquo;re viewing a reader&rsquo;s 2012 election scenario. Make your own:<a href="#" class="ink-resetButton">Start Now</a></div>';
    INK.$('.ink-shareDesc').hide().after(html);
  }

};

